//dieter whittingham
//jan 7 2023
//filename Intervals.js

import React from 'react';
import keyboard from '../keyboard';
import { useState, useEffect } from 'react';

function Header(){
    return (
        <>
            <h1>Interval Training</h1>
            <p>Welcome to interval training. When you press play, you will be given
                a series of random intervals. Identify them using the drop down box.
            </p>    
        </>
        );
}

function PlayInterval(key1, key2){
    const key1audio = new Audio(key1);
    const key2audio = new Audio(key2);
    key1audio.play();
    key2audio.play();
}

const Intervals = () => {
    useEffect( () => {PlayInterval('./sounds/piano-88-notes/4c.wav',
    './sounds/piano-88-notes/4g.wav')}, []);
    return(
    <>
        <Header />
    </>
    )
}



export default Intervals;
